import math; exec("for i in range(1, int(input()) + 1): a, b = map(int, input().split(':')); h = math.atan(1.0 * a / b); r = 200 / (2 * math.sin(h) + math.pi - 2 * h); print(f'Case {i}: {2 * r * math.sin(h):.10f} {2 * r * math.cos(h):.10f}')") 
